mapper.select:
SELECT GOODS_NO, NAME, CONTENT, READCNT, REGDATE, PRICE, RELATED, KEYWORD, CATEGORY, ATTRIBUTE_NO, GOODS_SIZE, COLOR, COUNT, RNUM, WM_CONCAT(SAV_NAME) AS IMAGE FROM ( select c.GOODS_NO, c.NAME, c.CONTENT, c.READCNT, c.REGDATE, c.PRICE, c.RELATED, c.KEYWORD, c.CATEGORY, c.ATTRIBUTE_NO, c.GOODS_SIZE, c.COLOR, c.COUNT, c.RNUM, d.SAV_NAME FROM
(SELECT GOODS_NO, NAME, CONTENT, READCNT, REGDATE, PRICE, RELATED, KEYWORD, CATEGORY, ATTRIBUTE_NO, WM_CONCAT(GOODS_SIZE) AS GOODS_SIZE, WM_CONCAT(COLOR) AS COLOR, WM_CONCAT(COUNT) AS COUNT, ROW_NUMBER() OVER (ORDER BY GOODS_NO DESC) RNUM FROM ( SELECT a.GOODS_NO, a.NAME, a.CONTENT, a.READCNT, a.REGDATE, a.PRICE, a.RELATED, a.KEYWORD, a.CATEGORY, b.ATTRIBUTE_NO, b.GOODS_SIZE, b.COLOR, b.COUNT FROM GOODS a INNER JOIN GOODS_ATTRIBUTE b ON a.GOODS_NO = b.GOODS_NO
) GROUP BY GOODS_NO, NAME,CONTENT, READCNT, REGDATE, PRICE, RELATED, KEYWORD, ATTRIBUTE_NO, CATEGORY
) c INNER JOIN UPLOAD d ON c.GOODS_NO=d.GOODS_NO ) GROUP BY GOODS_NO, NAME, CONTENT, READCNT, REGDATE, PRICE, RELATED, KEYWORD, CATEGORY, ATTRIBUTE_NO, GOODS_SIZE, COLOR, COUNT, RNUM

SELECT GOODS_NO, NAME, PRICE, WM_CONCAT(SAV_NAME) IMAGE FROM (SELECT a.GOODS_NO, a.NAME, a.PRICE, b.SAV_NAME FROM GOODS a INNER JOIN UPLOAD b ON a.GOODS_NO = b.GOODS_NO ) WHERE GOODS_NO IN
#{item}
GROUP BY GOODS_NO, NAME, PRICE

select REVIEW_NO, TITLE, MEMBER_ID, CONTENT, REGDATE, READCNT, GOODS_NO, WM_CONCAT(DISTINCT SAV_NAME) IMAGE, ROW_NUMBER() OVER (ORDER BY REVIEW_NO DESC) RNUM FROM (SELECT a.REVIEW_NO, a.TITLE, a.MEMBER_ID, a.CONTENT, a.REGDATE, a.READCNT, a.GOODS_NO, b.SAV_NAME FROM UPLOAD b INNER JOIN REVIEW a ON a.GOODS_NO=b.GOODS_NO WHERE a.GOODS_NO = ${GOODS_NO}
ORDER BY REVIEW_NO
) GROUP BY REVIEW_NO, TITLE, MEMBER_ID, CONTENT, REGDATE, READCNT, GOODS_NO

select QA_NO, CATEGORY, TITLE, PASSWD, MEMBER_ID, CONTENT, STATUS, REGDATE, GOODS_NO, WM_CONCAT(DISTINCT SAV_NAME) IMAGE, ROW_NUMBER() OVER (ORDER BY QA_NO DESC) RNUM FROM (SELECT a.QA_NO, a.CATEGORY, a.TITLE, a.PASSWD, a.MEMBER_ID, a.CONTENT, a.STATUS, a.REGDATE, a.GOODS_NO, b.SAV_NAME FROM UPLOAD b INNER JOIN QA a ON a.GOODS_NO=b.GOODS_NO WHERE a.GOODS_NO = ${GOODS_NO}
ORDER BY QA_NO
) GROUP BY QA_NO, CATEGORY, TITLE, PASSWD, MEMBER_ID, CONTENT, STATUS, REGDATE, GOODS_NO
