mapper.update:

UPDATE 
	MEMBER 
SET
	POINT = POINT + #{ADDPOINT}
WHERE
	MEMBER_ID = #{MEMBER_ID}


UPDATE 
	MEMBER 
SET
	POINT = POINT - #{USEPOINT}
WHERE
	MEMBER_ID = #{MEMBER_ID}

UPDATE order_3t SET state = state+1
,REGDATE=SYSDATE
WHERE ORDER_NO = #{ORDER_NO}


UPDATE 
	ORDER_3T
SET
	STATE = #{selectState}	
WHERE
	ORDER_NO=#{ORDER_NO}
