mapper.delete:

delete FROM
    REVIEW_COMMENT
WHERE
    REVIEW_COMMENT_NO = #{REVIEW_COMMENT_NO}   
AND
	PASSWD = #{PASSWD}

mapper.insert:
INSERT INTO REVIEW_COMMENT ( REVIEW_COMMENT_NO, REVIEW_ORIGIN_NO, NAME, PASSWD, CONTENT, REGDATE
) VALUES ( REVIEW_COMMENT_NO_SEQ.NEXTVAL, #{REVIEW_ORIGIN_NO}, #{NAME}, #{PASSWD}, #{CONTENT}, SYSDATE
)
